Table	Create Table
users	CREATE TABLE `users` (\n  `user_id` int(10) unsigned NOT NULL AUTO_INCREMENT,\n  `username` varchar(20) NOT NULL,\n  `password_hash` varchar(255) NOT NULL,\n  `email` varchar(50) NOT NULL,\n  PRIMARY KEY (`user_id`),\n  UNIQUE KEY `username` (`username`),\n  UNIQUE KEY `email` (`email`)\n) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
comments	CREATE TABLE `comments` (\n  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,\n  `post_id` int(10) unsigned NOT NULL,\n  `user_id` int(10) unsigned NOT NULL,\n  `text` varchar(200) NOT NULL,\n  PRIMARY KEY (`id`),\n  KEY `user_id` (`user_id`),\n  KEY `idx_post_id` (`post_id`),\n  CONSTRAINT `comments_ibfk_1` FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`),\n  CONSTRAINT `comments_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `users` (`user_id`)\n) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
posts	CREATE TABLE `posts` (\n  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,\n  `user_id` int(10) unsigned NOT NULL,\n  `title` varchar(200) NOT NULL,\n  `body` varchar(2000) NOT NULL,\n  `link` varchar(300) DEFAULT NULL,\n  `created` timestamp NOT NULL DEFAULT current_timestamp(),\n  `vote_count` int(11) DEFAULT 0,\n  PRIMARY KEY (`id`),\n  KEY `user_id` (`user_id`),\n  CONSTRAINT `posts_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`user_id`)\n) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
votes	CREATE TABLE `votes` (\n  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,\n  `user_id` int(10) unsigned NOT NULL,\n  `story_id` int(10) unsigned NOT NULL,\n  `vote_value` tinyint(4) NOT NULL,\n  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),\n  PRIMARY KEY (`id`),\n  UNIQUE KEY `unique_vote` (`user_id`,`story_id`),\n  KEY `story_id` (`story_id`),\n  CONSTRAINT `votes_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`user_id`) ON DELETE CASCADE,\n  CONSTRAINT `votes_ibfk_2` FOREIGN KEY (`story_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE\n) ENGINE=InnoDB AUTO_INCREMENT=20 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci
